#include "fsl_device_registers.h"
#include "fsl_port_hal.h"
#include "pin_mux.h"

void configure_gpio_pins(uint32_t instance)
{
  switch(instance) {
    case PORTA_IDX:                      /* PTA */
      /* Affects PORTA_PCR19 register */
      PORT_HAL_SetPassiveFilterCmd(PORTA,19u,true);
      PORT_HAL_SetMuxMode(PORTA,19u,kPortMuxAsGpio);
      PORT_HAL_SetPullMode(PORTA,19u,kPortPullUp);
      PORT_HAL_SetPullCmd(PORTA,19u,true);
      break;
    case PORTD_IDX:                      /* PTD */
      /* Affects PORTD_PCR1 register */
      PORT_HAL_SetPassiveFilterCmd(PORTD,1u,true);
      PORT_HAL_SetMuxMode(PORTD,1u,kPortMuxAsGpio);
      PORT_HAL_SetPullMode(PORTD,1u,kPortPullUp);
      PORT_HAL_SetPullCmd(PORTD,1u,true);
      /* Affects PORTD_PCR4 register */
      PORT_HAL_SetMuxMode(PORTD,4u,kPortMuxAsGpio);
      /* Affects PORTD_PCR5 register */
      PORT_HAL_SetMuxMode(PORTD,5u,kPortMuxAsGpio);
      /* Affects PORTD_PCR6 register */
      PORT_HAL_SetMuxMode(PORTD,6u,kPortMuxAsGpio);
      /* Affects PORTD_PCR7 register */
      PORT_HAL_SetMuxMode(PORTD,7u,kPortMuxAsGpio);
      break;
    case PORTE_IDX:                      /* PTE */
      /* Affects PORTE_PCR3 register */
      PORT_HAL_SetPassiveFilterCmd(PORTE,3u,true);
      PORT_HAL_SetMuxMode(PORTE,3u,kPortMuxAsGpio);
      PORT_HAL_SetPullMode(PORTE,3u,kPortPullUp);
      PORT_HAL_SetPullCmd(PORTE,3u,true);
      /* Affects PORTE_PCR4 register */
      PORT_HAL_SetPassiveFilterCmd(PORTE,4u,true);
      PORT_HAL_SetMuxMode(PORTE,4u,kPortMuxAsGpio);
      PORT_HAL_SetPullMode(PORTE,4u,kPortPullUp);
      PORT_HAL_SetPullCmd(PORTE,4u,true);
      break;
    default:
      break;
  }
}

void configure_i2c_pins(uint32_t instance)
{
  switch(instance) {
    case I2C0_IDX:                       /* I2C0 */
      /* Affects PORTD_PCR2 register */
      PORT_HAL_SetMuxMode(PORTD,2u,kPortMuxAlt4);
      PORT_HAL_SetOpenDrainCmd(PORTD,2u,true);
      /* Affects PORTD_PCR3 register */
      PORT_HAL_SetMuxMode(PORTD,3u,kPortMuxAlt4);
      PORT_HAL_SetOpenDrainCmd(PORTD,3u,true);
      break;
    default:
      break;
  }
}

