#define ENET_RMW_EIR(base, mask, value) (ENET_WR_EIR(base, (ENET_RD_EIR(base) & ~(mask)) | (value)))
#define ENET_SET_EIR(base, value) (ENET_WR_EIR(base, ENET_RD_EIR(base) |  (value)))
