#define I2S_BWR_TCR4_FCOMB(base, value) (BME_BFI32(&I2S_TCR4_REG(base), ((uint32_t)(value) << I2S_TCR4_FCOMB_SHIFT), I2S_TCR4_FCOMB_SHIFT, I2S_TCR4_FCOMB_WIDTH))
