#define SDHC_BWR_XFERTYP_DMAEN(base, value) (BME_BFI32(&SDHC_XFERTYP_REG(base), ((uint32_t)(value) << SDHC_XFERTYP_DMAEN_SHIFT), SDHC_XFERTYP_DMAEN_SHIFT, SDHC_XFERTYP_DMAEN_WIDTH))
