#define SDHC_BRD_XFERTYP_AC12EN(base) (BME_UBFX32(&SDHC_XFERTYP_REG(base), SDHC_XFERTYP_AC12EN_SHIFT, SDHC_XFERTYP_AC12EN_WIDTH))

