#define SDHC_BWR_SYSCTL_SDCLKFS(base, value) (BME_BFI32(&SDHC_SYSCTL_REG(base), ((uint32_t)(value) << SDHC_SYSCTL_SDCLKFS_SHIFT), SDHC_SYSCTL_SDCLKFS_SHIFT, SDHC_SYSCTL_SDCLKFS_WIDTH))
