#define MCM_CLR_MATCR(base, index, value) (MCM_WR_MATCR(base, index, MCM_RD_MATCR(base, index) & ~(value)))
#define MCM_TOG_MATCR(base, index, value) (MCM_WR_MATCR(base, index, MCM_RD_MATCR(base, index) ^  (value)))
/*@}*/

