#define TMR_CLR_FILT(base, value) (BME_AND16(&TMR_FILT_REG(base), (uint16_t)(~(value))))
#define TMR_TOG_FILT(base, value) (BME_XOR16(&TMR_FILT_REG(base), (uint16_t)(value)))
/*@}*/

