#define FTM_BWR_SC_PS(base, value) (BME_BFI32(&FTM_SC_REG(base), ((uint32_t)(value) << FTM_SC_PS_SHIFT), FTM_SC_PS_SHIFT, FTM_SC_PS_WIDTH))
/*@}*/

